/*
 * Главный файл стилей.
 * Все паршалы подключаются сюда.
 */

// utils
@import './utils/variables';
@import './utils/visually-hidden';

// base
@import './base/common';

// components
@import './components/section';
@import './components/container';
@import './components/mobile-menu';
@import './components/button';
@import './components/about-modal';
@import './components/socials';
@import './components/backdrop';
@import './components/modal';
@import './components/modal-form';

// layout
@import './layout/hero';
@import './layout/header';
@import './layout/products';
@import './layout/about';
@import './layout/advantages';
@import './layout/gallery';
@import './layout/customer-reviews';
@import './layout/contacts';
@import './layout/footer';

sources.sass\\utils\\_visually-hidden.scss:
.visually-hidden {
  position: absolute;
  white-space: nowrap;
  width: 1px;
  height: 1px;
  overflow: hidden;
  border: 0;
  padding: 0;
  clip: rect(0 0 0 0);
  clip-path: inset(50%);
  margin: -1px;
}

