sources.sass\\components\\_about-modal.scss:
/* modal */
.backdrop {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 3;
  opacity: 1;
  transition: opacity 222ms cubic-bezier(0.57, 0.33, 0, 0.64);

  width: 100vw;
  height: 100vh;

  background-color: rgba(0, 0, 0, 0.5);
  &.is-hidden {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
  }
}
.about-modal {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) scale(1);
  transition: transform 222ms cubic-bezier(0.57, 0.33, 0, 0.64);

  text-align: center;
  overflow-y: scroll;
  height: calc(100vh - 55px);

  padding-top: 33px;
  padding-right: 33px;
  padding-bottom: 33px;
  padding-left: 33px;

  background-image: linear-gradient(0deg, var(--primary-bg-color), var(--secondary-bg-color));
  // background-color: var(--primary-bg-color);
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 13px;

  .backdrop.is-hidden & {
    transform: translate(-50%, -50%) scale(0.3);
  }
  @media screen and (max-width: 767px) {
    width: calc(100vw - 30px);
  }
  @media screen and (min-width: 768px) {
    width: 700px;
    // height: 555px;
  }
  @media screen and (min-width: 1280px) {
    width: 1050px;
  }
}

.about-modal__button {
  position: absolute;
  top: 13px;
  right: 13px;

  display: flex;
  justify-content: center;
  align-items: center;
  width: 33px;
  height: 33px;

  background-color: var(--primary-bg-color);
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 50%;

  color: var(--secondary-button-color);

  transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1),
    background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    color: var(--primary-button-color);
    background-color: var(--secondary-button-color);
  }
}
.modal__icon {
  // display: block;
  //   fill: currentColor;
  stroke: currentColor;
}

.about-modal__title {
  margin-bottom: 13px;

  font-weight: 500;
  font-size: 12px;
  line-height: 1.62;
  color: var(--second-text-color);

  @media screen and (min-width: 768px) {
    margin-bottom: 15px;

    font-size: 18px;
    line-height: 1.76;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 18px;

    font-size: 22px;
    line-height: 1.76;
    letter-spacing: 0.04em;
  }
}

.about-modal__text {
  margin-top: 13px;

  font-weight: 500;
  font-size: 12px;
  line-height: 1.62;
  color: var(--third-text-color);

  @media screen and (min-width: 768px) {
    margin-top: 15px;

    font-size: 15px;
    line-height: 1.76;
    letter-spacing: 0.04em;
  }
  @media screen and (min-width: 1280px) {
    margin-top: 18px;
    font-size: 18px;
    line-height: 1.94;
  }
}

.about-modal__list {
  margin-top: 22px;
  margin-left: auto;
  margin-right: auto;

  @media screen and (min-width: 1280px) {
    display: flex;
    justify-content: center;
  }
}
.about-modal__item {
  &:not(:last-child) {
    margin-bottom: 22px;
  }
  @media screen and (min-width: 1280px) {
    width: calc((100% - 2 * 30px) / 2);
    &:not(:last-child) {
      margin-right: 30px;
    }
  }
}

.about-modal__img {
  border-radius: 10px;
  margin-left: auto;
  margin-right: auto;
}

.about-modal__subtitle {
  margin-top: 14px;
  margin-bottom: 13px;

  font-weight: 500;
  font-size: 11px;
  line-height: 1.62;
  color: var(--second-text-color);

  @media screen and (min-width: 768px) {
    margin-top: 16px;
    margin-bottom: 15px;

    font-size: 15px;
    line-height: 1.76;
  }
  @media screen and (min-width: 1280px) {
    margin-top: 20px;
    margin-bottom: 18px;

    font-size: 18px;
    line-height: 1.76;
    letter-spacing: 0.04em;
  }
}

