  background-color: #FFB8CA;
  padding-top: 6px;

  @media screen and (min-width: 768px) {
    padding: 37px 0 0 0;
  }
}

.header-container {
  display: flex;
  justify-content: space-between;
  align-items: center;

  @media screen and (min-width: 1280px) {
    position: relative;
  }
}

.header-link {
  @media screen and (min-width: 1280px) {
    position: absolute;
    left: -54px;
  }
}

.header-nav {
  position: relative;
  z-index: 3;

  @media screen and (max-width: 1279px) {
    display: none;
  }

  @media screen and (min-width : 1280px) {
    margin-left: auto;
  }
}

.header-nav__list {
  display: flex;
}

.header-nav__item {
  &:not(:last-child) {
    margin-right: 35px;
  }
}

.header-nav__link {
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;

  color: var(--primary-button-color);
