.products {
  padding-top: 121px;
  @media screen and (min-width: 1280px) {
    padding-top: 127px;
  }
}
.products__list {
  margin-top: 144px;

  padding-bottom: 98px;

  list-style: none;
  margin-left: auto;
  margin-right: auto;
  @media screen and (min-width: 768px) {
    margin-top: 150px;
    display: flex;
    flex-wrap: wrap;
    margin-right: -10px;
    margin-left: -10px;
  }
  @media screen and (min-width: 1280px) {
    margin-top: 259px;
  }
}
.products__item {
  @media screen and (max-width: 767px) {
    display: block;
    position: relative;
    width: 280px;
    height: 485px;
    border-radius: 24px;
    margin-left: auto;
    margin-right: auto;
    &:not(:last-child) {
      margin-bottom: 130px;
    }
  }
  @media screen and (min-width: 768px) {
    display: block;
    position: relative;
    border-radius: 24px;
    width: 220px;
    height: 485px;
    margin-right: 10px;
    margin-left: 10px;
    flex-basis: calc(100% / 3 - 20px);
  }
  @media screen and (min-width: 1280px) {
    width: 329px;
    height: 525px;
  }
}
.product-1 {
  background: #ffb8ca;
}
.product-1::before {
  content: '';
  position: absolute;
  width: 249px;
  height: 252px;

  background-repeat: no-repeat;
  background-image: url(../images/products/product-1-mobile.png);
  left: 15px;
  // left: 50%;
  // transform: translateX(-50%);
  // top: -210px;
  bottom: 346px;
  @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
    .product-1::before {
      background-image: url(../images/products/product-1-mobile-2x.png);
    }
  }
  @media screen and (min-width: 768px) {
    width: 220px;
    height: 226px;
    background-image: url('../images/products/product-1-tablet.png');
    left: 0;
    bottom: 363px;
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      .product-1::before {
        background-image: url('../images/products/product-1-tablet-2x.png');
      }
    }
  }
  @media screen and (min-width: 1280px) {
    width: 335px;
    height: 335px;
    background-image: url('../images/products/product-1-desctop.png');
    bottom: 400px;
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
