.location-popup {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.4);
  z-index: 100;

  &.is-open {
    display: block;
  }

  & .body {
    width: 100%;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
      0px 2px 1px rgba(0, 0, 0, 0.2);
    border-radius: 4px;

    opacity: 1;
    padding: 35px 15px 35px 15px;
    overflow: hidden;
    background-color: #fff5f6;

    @media screen and (max-width: 767px) {
      max-width: 480px;

      position: relative;
    }

    @media screen and (min-width: 768px) and (max-width: 1279px) {
      max-width: 600px;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%) scale(1);
    }
    @media screen and (min-width: 1280px) {
      max-width: 768px;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%) scale(1);
    }

    &__close-button {
      position: absolute;
      display: flex;
      align-items: center;
      justify-content: center;
      margin: 0;
      padding: 0;
      top: 10px;
      right: 10px;
      border: none;
      background-color: transparent;
      cursor: pointer;
    }

    &__close-icon {
      width: 20px;
      height: 20px;
      stroke: var(--second-text-color);
    }
  }
  &__iframe {
    width: 100%;
    height: 450px;
  }
}

