.footer {
  padding-top: 47px;
  padding-bottom: 25px;
  background-color: var(--primary-bg-color);
  @media screen and (min-width: 768px) {
    padding-top: 99px;
    padding-bottom: 18px;
  }
  @media screen and (min-width: 1280px) {
    padding-top: 121px;
    padding-bottom: 10px;
  }
}
.container-footer-top {
  @media screen and (min-width: 768px) {
    margin-bottom: 91px;
    display: flex;
    justify-content: space-between;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 97px;
  }
}
.container-footer-title {
  margin-bottom: 43px;
  @media screen and (min-width: 768px) {
    margin-bottom: 0;
  }
}
.footer__title {
  margin-bottom: 17px;
  width: 232px;
  color: var(--fourth-text-color);
  font-family: Titan One;
  font-size: 18px;
  line-height: 1.167;
  text-transform: uppercase;
  @media screen and (min-width: 768px) {
    margin-bottom: 16px;
    width: 340px;
    font-size: 26px;
    line-height: 1.145;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 15px;
    width: 472px;
    font-size: 30px;
    line-height: 1.133;
  }
}
.footer__title-accent {
  width: 200px;
  color: var(--accent-color);
  font-family: Titan One;
  font-size: 34px;
  line-height: 1.147;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  @media screen and (min-width: 768px) {
    width: 340px;
    font-size: 48px;
    line-height: 1.145;
  }
  @media screen and (min-width: 1280px) {
    width: 472px;
    font-size: 58px;
    line-height: 1.137;
  }
}
.container-footer-socials {
  margin-bottom: 43px;
  @media screen and (min-width: 768px) {
    margin-bottom: 0;
  }
}
.footer__link {
  display: block;
  color: var(--second-text-color);
  font-weight: 700;
  font-size: 12px;
  line-height: 1.86;
  text-align: right;
  @media screen and (min-width: 768px) {
    font-size: 14px;
  }
  @media screen and (min-width: 1280px) {
    font-size: 16px;
  }
}
.footer__link-accent {
  color: var(--accent-color);
}
.container-footer-bottom::before {
  content: '';
  display: block;
  width: 100%;
  height: 1px;
  background-color: #e1e1e1;
}
.footer__copiryght {
  padding-top: 22px;
  margin-left: auto;
  margin-right: auto;
  width: 140px;
  color: var(--third-text-color);
  font-weight: 700;
  font-size: 14px;
  line-height: 1.52;
  text-align: center;
  @media screen and (min-width: 768px) {
    padding-top: 29px;
    width: 276px;
    line-height: 1.85;
  }
  @media screen and (min-width: 1280px) {
    padding-top: 9px;
    line-height: 1.86;
  }
}
